// client/src/pages/Profile/Profile.js
import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import './Profile.css';

export default function Profile() {
  const [profiles, setProfiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { search } = useLocation();
  const user = new URLSearchParams(search).get('user'); // 'maria' or 'yazan'

  useEffect(() => {
    fetch('http://localhost:5000/api/portfolio/profile')
      .then(res => {
        if (!res.ok) throw new Error(`Status ${res.status}`);
        return res.json();
      })
      .then(data => setProfiles(data))
      .catch(err => setError(err.message))
      .finally(() => setLoading(false));
  }, []);

  if (loading) {
    return (
      <div className="profile-container">
        <p className="profile-loading">Loading profile…</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="profile-container">
        <p className="profile-error">Error loading profile: {error}</p>
      </div>
    );
  }

  const entry = profiles.find(item => item.owner === user);
  if (!entry) {
    return (
      <div className="profile-container">
        <p className="profile-error">Profile not found for “{user}”</p>
      </div>
    );
  }

  const {
    personal,
    education,
    experience,
    projects,
    skills,
    certifications,
    awards,
    volunteerExperience,
    openSourceContributions,
    publications,
    references,
    interests
  } = entry.profile;

  return (
    <div className="profile-container">
      <header className="profile-header">
        <img
          src={personal.photoUrl}
          alt={personal.fullName}
          className="profile-photo"
        />
        <h1 className="profile-name">{personal.fullName}</h1>
        <p className="profile-title">{personal.title}</p>
        <p className="profile-location">{personal.location}</p>
        <p className="profile-summary">{personal.summary}</p>
        <div className="profile-contact">
          <a href={`mailto:${personal.email}`}>{personal.email}</a>
          <span> | </span>
          <a href={`tel:${personal.phone}`}>{personal.phone}</a>
        </div>
        <div className="profile-social">
          {personal.website && (
            <a href={personal.website} target="_blank" rel="noreferrer">
              Website
            </a>
          )}
          {personal.linkedIn && (
            <a href={personal.linkedIn} target="_blank" rel="noreferrer">
              LinkedIn
            </a>
          )}
          {personal.github && (
            <a href={personal.github} target="_blank" rel="noreferrer">
              GitHub
            </a>
          )}
        </div>
      </header>

      {/* Education */}
      <section className="profile-section">
        <h2>Education</h2>
        <ul>
          {education.map((ed, i) => (
            <li key={i}>
              <strong>{ed.institution}</strong> — {ed.degree} (
              {ed.startDate} to {ed.endDate})
              {ed.fieldOfStudy && <span>, {ed.fieldOfStudy}</span>}
              {ed.gpa && <span>, GPA: {ed.gpa}</span>}
              {ed.honors && <p className="small">{ed.honors}</p>}
            </li>
          ))}
        </ul>
      </section>

      {/* Experience */}
      <section className="profile-section">
        <h2>Experience</h2>
        <ul>
          {experience.map((exp, i) => (
            <li key={i}>
              <strong>{exp.company || exp.project}</strong> — {exp.role} (
              {exp.startDate} to {exp.endDate})
              <ul className="nested-list">
                {exp.responsibilities.map((r, j) => (
                  <li key={j}>{r}</li>
                ))}
              </ul>
              {exp.achievements?.length > 0 && (
                <p className="small">
                  Achievements: {exp.achievements.join(', ')}
                </p>
              )}
            </li>
          ))}
        </ul>
      </section>

      {/* Projects */}
      <section className="profile-section">
        <h2>Projects</h2>
        <ul>
          {projects.map((proj, i) => (
            <li key={i}>
              <strong>
                {proj.url ? (
                  <a href={proj.url} target="_blank" rel="noreferrer">
                    {proj.name}
                  </a>
                ) : (
                  proj.name
                )}
              </strong>{' '}
              — {proj.role}
              <p>{proj.description}</p>
              <p className="small">
                Tech: {proj.technologies.join(', ')}
                {proj.metrics &&
                  ` • Metrics: ${Object.entries(proj.metrics)
                    .map(([k, v]) => `${k}: ${v}`)
                    .join(', ')}`}
              </p>
            </li>
          ))}
        </ul>
      </section>

      {/* Skills */}
      <section className="profile-section">
        <h2>Skills</h2>
        {Object.entries(skills).map(([category, list]) => (
          <div key={category}>
            <h3 className="subheading">{category}</h3>
            <ul className="inline-list">
              {list.map((s, i) =>
                typeof s === 'string' ? (
                  <li key={i}>{s}</li>
                ) : (
                  <li key={i}>
                    {s.name} ({s.level}%)
                  </li>
                )
              )}
            </ul>
          </div>
        ))}
      </section>

      {/* Certifications */}
      <section className="profile-section">
        <h2>Certifications</h2>
        <ul>
          {certifications.map((c, i) => (
            <li key={i}>
              {c.name} — {c.issuer} ({c.date})
            </li>
          ))}
        </ul>
      </section>

      {/* Awards */}
      <section className="profile-section">
        <h2>Awards</h2>
        <ul>
          {awards.map((a, i) => (
            <li key={i}>
              <strong>{a.title}</strong> ({a.date}) — {a.description}
            </li>
          ))}
        </ul>
      </section>

      {/* Volunteer */}
      <section className="profile-section">
        <h2>Volunteer Experience</h2>
        <ul>
          {volunteerExperience.map((v, i) => (
            <li key={i}>
              <strong>{v.organization}</strong> — {v.role} ({v.date})
              <p>{v.description}</p>
            </li>
          ))}
        </ul>
      </section>

      {/* Open Source */}
      <section className="profile-section">
        <h2>Open Source Contributions</h2>
        <ul>
          {openSourceContributions.map((o, i) => (
            <li key={i}>
              Repo: {o.repo} — {o.contributions} PRs ({o.date})
            </li>
          ))}
        </ul>
      </section>

      {/* Publications */}
      <section className="profile-section">
        <h2>Publications</h2>
        <ul>
          {publications.map((p, i) => (
            <li key={i}>
              <strong>{p.title}</strong> — {p.publisher} ({p.date})
              {p.url && (
                <>
                  {' '}
                  <a href={p.url} target="_blank" rel="noreferrer">
                    [Link]
                  </a>
                </>
              )}
            </li>
          ))}
        </ul>
      </section>

      {/* References */}
      <section className="profile-section">
        <h2>References</h2>
        <ul>
          {references.map((r, i) => (
            <li key={i}>
              {r.name}, {r.relationship} — {r.contact}
            </li>
          ))}
        </ul>
      </section>

      {/* Interests */}
      <section className="profile-section">
        <h2>Interests</h2>
        <ul className="inline-list">
          {interests.map((int, i) => (
            <li key={i}>{int}</li>
          ))}
        </ul>
      </section>
    </div>
  );
}
