import React from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import './Home.css';

const Home = () => {
  const navigate = useNavigate();

  const handleSelect = (user) => {
    navigate(`/projects?user=${user}`);
  };

  return (
    <div className="container home-wrapper">
      <motion.h1 
        className="home-title"
        initial={{ opacity: 0, y: -30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        Welcome to Our Portfolio
      </motion.h1>

      <motion.p 
        className="home-subtitle"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.4 }}
      >
        Choose a profile to explore:
      </motion.p>

      <div className="row justify-content-center profile-grid">
        <motion.div 
          className="col-md-4 profile-card"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={() => handleSelect('maria')}
        >
          <img src="https://via.placeholder.com/150" alt="Maria Elheeb" className="profile-img" />
          <h3>Maria Elheeb</h3>
          <p>Fullstack Developer | React | Node.js</p>
        </motion.div>

        <motion.div 
          className="col-md-4 profile-card"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={() => handleSelect('yazan')}
        >
          <img src="icon1.png" alt="Yazan Dawud" className="profile-img" />
          <h3>Yazan Dawud</h3>
          <p>Software Engineer | JavaScript | Express</p>
        </motion.div>
      </div>
    </div>
  );
};

export default Home;
