// src/pages/Profile/Profile.js
import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Box, Typography, Avatar, Button } from '@mui/material';
import './Profile.css';

// mirror the Home.js profiles but add a short bio
const PROFILES = {
  maria: {
    name: 'Maria Elheeb',
    role: 'Fullstack Developer | React | Node.js',
    img: '/icon2.png',
    bio: `I’m a third-year CS student at Tel Hai College with hands-on experience building
          scalable full-stack apps using MERN, Bootstrap 5, and Material UI.
          I love crafting pixel-perfect UIs and clean API designs.`,
  },
  yazan: {
    name: 'Yazan Dawud',
    role: 'Software Engineer | JavaScript | Express',
    img: '/icon1.png',
    bio: `Passionate backend engineer specializing in Node.js, Express, and real-time systems.
          I enjoy optimizing performance, architecting microservices, and mentoring peers.`,
  }
};

export default function Profile() {
  const { search } = useLocation();
  const navigate = useNavigate();
  const userKey = new URLSearchParams(search).get('user');
  const profile = PROFILES[userKey];

  if (!profile) {
    return (
      <Box className="profile-page">
        <Typography variant="h5" color="error" align="center">
          Profile not found
        </Typography>
      </Box>
    );
  }

  return (
    <Box className="profile-page">
      <Avatar
        src={profile.img}
        alt={profile.name}
        className="profile-avatar-large"
      />
      <Typography variant="h3" gutterBottom>
        {profile.name}
      </Typography>
      <Typography variant="h6" gutterBottom className="profile-role">
        {profile.role}
      </Typography>
      <Typography variant="body1" paragraph className="profile-bio">
        {profile.bio}
      </Typography>
      <Button
        variant="outlined"
        onClick={() => navigate(`/projects?user=${userKey}`)}
      >
        View Projects
      </Button>
    </Box>
  );
}
