import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Header.css';

const Header = () => {
  const location = useLocation();
  const isActive = (path) => location.pathname === path ? 'nav-link active' : 'nav-link';

  return (
    <header className="custom-header shadow-sm">
      <nav className="navbar navbar-expand-lg navbar-light bg-white container">
        <Link className="navbar-brand fw-bold text-primary" to="/">
          <img src="icon_web.png" alt="Logo" width="32" className="me-2" />
          Maria & Yazan
        </Link>
        <div className="collapse navbar-collapse show justify-content-end">
          <ul className="navbar-nav">
            <li className="nav-item"><Link className={isActive('/')} to="/">Home</Link></li>
            <li className="nav-item"><Link className={isActive('/projects')} to="/projects">Projects</Link></li>
            <li className="nav-item"><Link className={isActive('/skills')} to="/skills">Skills</Link></li>
            <li className="nav-item"><Link className={isActive('/resume')} to="/resume">Resume</Link></li>
            <li className="nav-item"><Link className={isActive('/about')} to="/about">About</Link></li>
            <li className="nav-item"><Link className={isActive('/contact')} to="/contact">Contact</Link></li>
          </ul>
        </div>
      </nav>
    </header>
  );
};

export default Header;
