import { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import './Projects.css';

const Projects = () => {
  const [projects, setProjects] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const location = useLocation();

  const queryParams = new URLSearchParams(location.search);
  const selectedUser = queryParams.get('user'); // maria or yazan

  useEffect(() => {
    fetch('http://localhost:5000/api/portfolio/projects')
      .then(res => res.json())
      .then(data => {
        setProjects(data);

        if (selectedUser) {
          const filteredData = data.filter(p => p.owner === selectedUser);
          setFiltered(filteredData);
        } else {
          setFiltered(data); // no filter
        }
      });
  }, [selectedUser]);

  return (
    <div className="container py-5">
      <h1 className="text-center mb-4">Projects</h1>

      {filtered.length === 0 ? (
        <p className="text-center text-muted">No projects found for {selectedUser || 'this user'}.</p>
      ) : (
        <div className="row">
          {filtered.map(p => (
            <div key={p.id} className="col-md-6 col-lg-4 mb-4">
              <div className="card project-card">
                <div className="card-body">
                  <h5 className="card-title">{p.title}</h5>
                  <p className="card-text">{p.description}</p>
                  {p.link && (
                    <a href={p.link} className="btn btn-outline-primary" target="_blank" rel="noreferrer">
                      View Project
                    </a>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Projects;
