import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css'; // optional external CSS for better styling

const Home = () => {
  const navigate = useNavigate();

  const handleSelect = (user) => {
    // Redirect to a dynamic route or filtered page
    navigate(`/projects?user=${user}`);
  };

  return (
    <div className="home-container">
      <h1 className="home-title">Welcome to Our Portfolio</h1>
      <p className="home-subtitle">Choose a profile to explore:</p>

      <div className="profile-selection">
        <div className="profile-card" onClick={() => handleSelect('maria')}>
          <img
            src="https://via.placeholder.com/150"
            alt="Maria Elheeb"
            className="profile-image"
          />
          <h2>Maria Elheeb</h2>
          <p>Fullstack Developer | React | Node.js</p>
        </div>

        <div className="profile-card" onClick={() => handleSelect('yazan')}>
          <img
            src="https://via.placeholder.com/150"
            alt="Yazan Dawud"
            className="profile-image"
          />
          <h2>Yazan Dawud</h2>
          <p>Software Engineer | JavaScript | Express</p>
        </div>
      </div>
    </div>
  );
};

export default Home;
