import React from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { Box, Typography, Grid, Card, CardContent, Avatar, Button } from '@mui/material';
import './Home.css';

const profiles = [
  {
    key: 'maria',
    name: 'Maria Elheeb',
    role: 'Fullstack Developer | React | Node.js',
    img: 'icon2.png',
  },
  {
    key: 'yazan',
    name: 'Yazan Dawud',
    role: 'Software Engineer | JavaScript | Express',
    img: 'icon1.png',
  },
];

const Home = () => {
  const navigate = useNavigate();
  const handleSelect = (user) => {
    navigate(`/projects?user=${user}`);
  };

  return (
    <Box className="home-hero">
      <motion.div
        className="hero-content"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <Typography variant="h2" component="h1" gutterBottom>
          Welcome to Our Portfolio
        </Typography>
        <Typography variant="subtitle1" gutterBottom>
          Choose a profile to explore:
        </Typography>
      </motion.div>

      <Grid container justifyContent="center" spacing={4} className="profile-grid">
        {profiles.map((p) => (
          <Grid item key={p.key} xs={12} sm={6} md={4}>
            <motion.div
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <Card
                className="profile-card"
                onClick={() => handleSelect(p.key)}
                elevation={6}
              >
                <CardContent>
                  <Avatar
                    src={p.img}
                    alt={p.name}
                    className="profile-avatar"
                  />
                  <Typography variant="h5" component="h3" mt={2}>
                    {p.name}
                  </Typography>
                  <Typography variant="body2" mt={1}>
                    {p.role}
                  </Typography>
                  <Button
                    variant="outlined"
                    className="explore-button"
                    sx={{ mt: 3 }}
                  >
                    Explore
                  </Button>
                </CardContent>
              </Card>
            </motion.div>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Home;
