// src/pages/Skills/Skills.js
import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { Box, Typography, Grid, LinearProgress } from '@mui/material';
import './Skills.css';

export default function Skills() {
  const [skillsData, setSkillsData] = useState([]);
  const [loading, setLoading] = useState(true);
  const { search } = useLocation();
  const user = new URLSearchParams(search).get('user'); // 'maria' or 'yazan'

  useEffect(() => {
    fetch('http://localhost:5000/api/portfolio/skills')
      .then(res => res.json())
      .then(data => setSkillsData(data))
      .catch(err => console.error(err))
      .finally(() => setLoading(false));
  }, []);

  if (loading) {
    return (
      <Box className="skills-page">
        <Typography variant="h5" align="center" color="textSecondary">
          Loading skills…
        </Typography>
      </Box>
    );
  }

  const profile = skillsData.find(item => item.owner === user);

  if (!profile) {
    return (
      <Box className="skills-page">
        <Typography variant="h4" align="center" color="error">
          No skills found for “{user}”
        </Typography>
      </Box>
    );
  }

  return (
    <Box className="skills-page">
      <Typography
        variant="h3"
        align="center"
        gutterBottom
        className="skills-title"
      >
        {profile.owner.charAt(0).toUpperCase() + profile.owner.slice(1)}’s Skills
      </Typography>

      <Grid container spacing={4}>
        {profile.skills.map(section => (
          <Grid item xs={12} sm={6} md={4} key={section.category}>
            <Typography
              variant="h5"
              className="skills-category"
              gutterBottom
            >
              {section.category}
            </Typography>
            {section.skills.map(skill => (
              <Box key={skill.name} className="skill-item">
                <Typography variant="body1" className="skill-name">
                  {skill.name}
                </Typography>
                <LinearProgress
                  variant="determinate"
                  value={skill.level}
                  className="skill-progress"
                />
                <Typography variant="body2" className="skill-level">
                  {skill.level}%
                </Typography>
              </Box>
            ))}
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}
